  const API_images = 'https://pixabay.com/api/?key=42970270-5c418c11121e28ec5bd2aa090';

  const searchImages = async (title) => {
    const response = await fetch(`${API_images}&q=${title}&image_type=photo&pretty=true&orientation=horizontal`)
    const data = await response.json();
    // console.log(data.hits);
    return data.hits;
  }

  useEffect(() => {
